import React,{useState,useEffect} from 'react'
import { useDispatch, useSelector } from 'react-redux'
import regionApi from '../api/regionApi'




export default function countryAdd(props) {
    const Selregion = useSelector(state => state.region_id)
    const [region,setRegion] = useState([])
    const [display,setDisplay] = useState(false)
    const [refresh,setRefresh] = useState(false)
    const [values,setValues] = useState ({
       region_id : undefined,
       region_name : '',
   }) 
    useEffect(() => {
        regionApi.list().then(data => {
            setRegion(data)
        })
        setRefresh(false)
    },[refresh])

  return (
    <div>
        <form onSubmit={props.onSubmit}>
            <div>
                <label>country ID : </label>
                <input type="text" placeholder="country ID" 
                onChange = {props.handleOnChange('country_id')}/>
                <label>country Name : </label>
                <input type="text" placeholder="country Name" 
                onChange = {props.handleOnChange('country_name')}/>
                <div>
                <label>Category : </label>
                <select onChange={props.SelectOnChange}>
                    <option>Choice Category</option>
                    {
                        props.Region.map((values,index) =>
                        <option key={index}>{values}</option>
                        )
                    }
                </select>
                </div>
            </div>
            <div>
                <button type='submit'> Simpan </button>
                <button onClick={()=>props.setDisplay(false)}> Cancel </button>
            </div>
        </form>
    </div>
  )
}
